package android.app.mobilebattery.DataSource;import android.app.mobilebattery.Pojo.CashInHandPojo;import android.util.Log;import androidx.annotation.NonNull;import androidx.lifecycle.MutableLiveData;import androidx.paging.DataSource;import androidx.paging.PageKeyedDataSource;public class CashInHandDSFactory extends DataSource.Factory {    private MutableLiveData<PageKeyedDataSource<Integer, CashInHandPojo.CashInHandItem>> liveData=            new MutableLiveData<>();    private String type;    private int value;    public CashInHandDSFactory(String type, int value) {        this.type = type;        this.value = value;    }    public CashInHandDSFactory() {        type = "";    }    @NonNull    @Override    public DataSource create() {        CashInHandDataSource dataSource ;        if (!type.equals("")){            Log.d("12345","cash factory search"+ type);            dataSource = new CashInHandDataSource(type,value);        }else {            Log.d("12345","default cash factory search"+ type);            dataSource = new CashInHandDataSource();        }        liveData.postValue(dataSource);        return dataSource;    }    public MutableLiveData<PageKeyedDataSource<Integer, CashInHandPojo.CashInHandItem>> getLiveData() {        return liveData;    }}